import { Category, AttentionCheck } from '../types';

export const loadQuestionsData = async (): Promise<Category[]> => {
  try {
    // In a real application, this would fetch from your API
    // For now, we'll use the sample data structure
    const sampleData: Category[] = [
      {
        "category": "Interpersonal Relations",
        "subcategories": [
          {
            "subcategory": "Visiting and hospitality",
            "topics": [
              {
                "topic": "Etiquette in the reception of visitors",
                "questions": [
                  "In your region, what are the typical ways people prepare their homes for the arrival of guests? Describe three common preparations that are usually done, such as cleaning, decorating, arranging guest rooms, or any traditional practices.",
                  "In your region, what is the first most common thing a visitor does when they enter your house? Focus on actions and not greetings and provide two most common ones.",
                  "In your region, if applicable, what are some traditional gifts or souvenirs given to guests during their visit? Specify two most common ones.",
                  "In your region, what is the common proper etiquette for sending off a guest who is visiting from another city?",
                  "In your region, what specific rituals or traditions are followed when someone visits your home for the first time? Specify two most common.",
                  "In your region, what is considered the appropriate duration for a typical social visit to a friend's house?",
                  "In your region, what are the most common refreshments offered to guests immediately upon arrival?",
                  "In your region, how do hosts typically accommodate guests who arrive during meal times?",
                  "In your region, what are the traditional seating arrangements when receiving guests in your home?",
                  "In your region, what topics of conversation are typically avoided when entertaining guests?",
                  "In your region, how do people typically show respect to elderly visitors in their homes?",
                  "In your region, what are the common practices when guests want to help with household tasks?",
                  "In your region, what is the etiquette around taking photos with or of guests in your home?",
                  "In your region, how do hosts typically handle situations when guests overstay their welcome?",
                  "In your region, what are the customary practices for accommodating overnight guests?"
                ]
              },
              {
                "topic": "Occasions for visiting",
                "questions": [
                  "In your region, what types of situations are considered inappropriate for visiting others? Specify 2 most common ones.",
                  "In your region, how do people usually approach visiting friends or relatives, do they tend to arrive unannounced, or is it more common to give notice beforehand?",
                  "In your region, what are the 3 most common personal occasions for visiting relatives? Examples of personal occasions could include weddings, births, funerals, anniversaries, engagements, or major family decisions.",
                  "In your region, what types of situations make it appropriate to visit a friend or colleague at their workplace? Specify the most common situation.",
                  "In your region, during what types of occasions do people traditionally return to their hometown or village? Specify two most common ones.",
                  "In your region, what are the most appropriate times of day for making social visits?",
                  "In your region, during which festivals or religious occasions is it customary to visit neighbors and friends?",
                  "In your region, what occasions warrant bringing children along when visiting others?",
                  "In your region, when is it considered appropriate to visit someone who is ill or recovering?",
                  "In your region, what are the customary visiting practices during times of mourning or bereavement?",
                  "In your region, during what life events do extended family members typically gather for visits?",
                  "In your region, what are the traditional visiting customs during harvest or seasonal celebrations?",
                  "In your region, when is it appropriate to visit newlyweds after their wedding?",
                  "In your region, what occasions call for formal visits with advance notice and proper attire?",
                  "In your region, during what circumstances do people visit religious or community leaders?"
                ]
              }
            ]
          },
          {
            "subcategory": "Gift giving",
            "topics": [
              {
                "topic": "Appropriate gifts for different occasions",
                "questions": [
                  "In your region, what are the most common gifts given during wedding ceremonies? Specify three most popular ones.",
                  "In your region, what gifts are typically brought when visiting a newborn baby and their family?",
                  "In your region, what are appropriate gifts to bring when invited to someone's home for dinner?",
                  "In your region, what gifts are traditionally given during religious festivals? Name two most common ones.",
                  "In your region, what are suitable gifts for housewarming ceremonies?",
                  "In your region, what gifts are appropriate for birthday celebrations for different age groups?",
                  "In your region, what are traditional gifts given during coming-of-age ceremonies?",
                  "In your region, what gifts are typically offered to teachers or mentors as a sign of respect?",
                  "In your region, what are appropriate gifts for retirement celebrations?",
                  "In your region, what gifts are traditionally given during engagement ceremonies?",
                  "In your region, what are suitable gifts for graduation ceremonies?",
                  "In your region, what gifts are typically brought when visiting someone who is ill?",
                  "In your region, what are traditional gifts exchanged between business partners or colleagues?",
                  "In your region, what gifts are appropriate for funeral or mourning periods?",
                  "In your region, what are customary gifts given to guests by hosts during special occasions?"
                ]
              }
            ]
          },
          {
            "subcategory": "Etiquette",
            "topics": [
              {
                "topic": "Social manners and courtesy",
                "questions": [
                  "In your region, what are the most important rules of politeness when meeting someone for the first time?",
                  "In your region, how do people typically show respect to elders in social interactions?",
                  "In your region, what are the proper etiquette rules for dining with others in social settings?",
                  "In your region, what behaviors are considered rude or disrespectful in public spaces?",
                  "In your region, what are the appropriate ways to address people of different social positions?",
                  "In your region, what are the common courtesies expected in professional or formal settings?",
                  "In your region, what are the proper manners when using public transportation?",
                  "In your region, what etiquette is expected when attending religious or cultural ceremonies?",
                  "In your region, what are the appropriate behaviors when disagreeing with someone in public?",
                  "In your region, what courtesies are expected when borrowing or lending items?",
                  "In your region, what are the proper manners for personal hygiene and grooming in social settings?",
                  "In your region, what etiquette is expected when taking or making phone calls in public?",
                  "In your region, what are the appropriate ways to handle compliments and praise?",
                  "In your region, what behaviors demonstrate good manners in educational settings?",
                  "In your region, what courtesies are expected when participating in community events?"
                ]
              }
            ]
          }
        ]
      }
    ];
    
    return sampleData;
  } catch (error) {
    console.error('Error loading questions data:', error);
    throw new Error('Failed to load questions data');
  }
};

export const generateQuestionId = (
  categoryIndex: number,
  subcategoryIndex: number,
  topicIndex: number,
  questionIndex: number
): string => {
  return `${categoryIndex}-${subcategoryIndex}-${topicIndex}-${questionIndex}`;
};

export const parseQuestionId = (questionId: string) => {
  const [categoryIndex, subcategoryIndex, topicIndex, questionIndex] = questionId.split('-').map(Number);
  return { categoryIndex, subcategoryIndex, topicIndex, questionIndex };
};

export const formatTime = (seconds: number): string => {
  const mins = Math.floor(seconds / 60);
  const secs = seconds % 60;
  return `${mins}:${secs.toString().padStart(2, '0')}`;
};

export const validateAnswer = (answer: string): { isValid: boolean; message?: string } => {
  if (!answer || answer.trim().length === 0) {
    return { isValid: false, message: 'Please provide an answer' };
  }
  
  if (answer.trim().length < 10) {
    return { isValid: false, message: 'Please provide a more detailed answer (at least 10 characters)' };
  }
  
  if (answer.length > 5000) {
    return { isValid: false, message: 'Answer is too long (maximum 5000 characters)' };
  }
  
  return { isValid: true };
};

export const shouldShowAttentionCheck = (questionCount: number): boolean => {
  // Show attention check every 15 questions
  return questionCount > 0 && questionCount % 15 === 0;
};

export const generateAttentionCheck = (
  currentCategory: string,
  currentTopic: string
): AttentionCheck => {
  const checks = [
    {
      question: `What category are you currently answering questions about?`,
      options: [
        currentCategory,
        'Food and Cuisine',
        'Religious Practices',
        'Economic Activities'
      ],
      correctAnswer: 0
    },
    {
      question: `What topic within ${currentCategory} are you currently focusing on?`,
      options: [
        currentTopic,
        'Traditional Ceremonies',
        'Seasonal Celebrations',
        'Community Gatherings'
      ],
      correctAnswer: 0
    },
    {
      question: 'What type of questions are you answering in this survey?',
      options: [
        'Cultural practices and commonsense in your region',
        'Personal preferences and opinions',
        'Historical facts and dates',
        'Mathematical problems'
      ],
      correctAnswer: 0
    }
  ];
  
  const randomCheck = checks[Math.floor(Math.random() * checks.length)];
  
  // Shuffle options
  const shuffledOptions = [...randomCheck.options];
  const correctOption = shuffledOptions[randomCheck.correctAnswer];
  
  for (let i = shuffledOptions.length - 1; i > 0; i--) {
    const j = Math.floor(Math.random() * (i + 1));
    [shuffledOptions[i], shuffledOptions[j]] = [shuffledOptions[j], shuffledOptions[i]];
  }
  
  const newCorrectAnswer = shuffledOptions.indexOf(correctOption);
  
  return {
    question: randomCheck.question,
    options: shuffledOptions,
    correctAnswer: newCorrectAnswer,
    currentTopic,
    currentCategory
  };
};

export const debounce = <T extends (...args: any[]) => any>(
  func: T,
  wait: number
): ((...args: Parameters<T>) => void) => {
  let timeout: NodeJS.Timeout;
  
  return (...args: Parameters<T>) => {
    clearTimeout(timeout);
    timeout = setTimeout(() => func.apply(null, args), wait);
  };
};

export const getEstimatedTimeRemaining = (
  totalQuestions: number,
  completedQuestions: number,
  averageTimePerQuestion: number = 120 // 2 minutes in seconds
): string => {
  const remainingQuestions = totalQuestions - completedQuestions;
  const estimatedSeconds = remainingQuestions * averageTimePerQuestion;
  
  if (estimatedSeconds < 3600) {
    const minutes = Math.ceil(estimatedSeconds / 60);
    return `~${minutes} minute${minutes !== 1 ? 's' : ''} remaining`;
  } else {
    const hours = Math.ceil(estimatedSeconds / 3600);
    return `~${hours} hour${hours !== 1 ? 's' : ''} remaining`;
  }
};
