import React, { useState, useEffect, useRef } from 'react';
import { useForm } from '../context/FormContext.tsx';
import { validateAnswer } from '../utils/helpers.ts';
import AttentionCheck from './AttentionCheck.tsx';
import ProgressBar from './ProgressBar.tsx';

export default function QuestionForm() {
  const {
    state,
    saveResponse,
    navigateToNext,
    navigateToPrevious,
    getCurrentQuestionData,
    getTotalQuestionsInCurrentTopic,
    getCompletedQuestionsInCurrentTopic,
    resetSession
  } = useForm();

  const [answer, setAnswer] = useState('');
  const [culturalCommonsense, setCulturalCommonsense] = useState<boolean | null>(null);
  const [errors, setErrors] = useState<{ answer?: string; cultural?: string }>({});
  const [isSaving, setIsSaving] = useState(false);
  const [showAttentionCheck, setShowAttentionCheck] = useState(false);
  const [attentionCheck, setAttentionCheck] = useState<any>(null);
  const [startTime, setStartTime] = useState(Date.now());
  const [showSuccess, setShowSuccess] = useState(false);
  const [lastQuestionId, setLastQuestionId] = useState<string | null>(null);
  const [isNavigating, setIsNavigating] = useState(false);
  const [navigationDirection, setNavigationDirection] = useState<'next' | 'previous' | null>(null);
  const [showCelebration, setShowCelebration] = useState<string | null>(null);
  const textareaRef = useRef<HTMLTextAreaElement>(null);

  const currentQuestionData = getCurrentQuestionData();

  // Reset form when question changes
  useEffect(() => {
    if (currentQuestionData && currentQuestionData.questionId !== lastQuestionId) {
      console.log('Question changed, loading data for:', currentQuestionData.questionId);
      
      // Reset form state
      setErrors({});
      setShowSuccess(false);
      setStartTime(Date.now());
      setLastQuestionId(currentQuestionData.questionId);
      setIsNavigating(false);
      setNavigationDirection(null);
      
      // Load existing response or reset
      const existingResponse = state.responses.get(currentQuestionData.questionId);
      if (existingResponse) {
        console.log('Found existing response:', existingResponse);
        setAnswer(existingResponse.answer);
        setCulturalCommonsense(existingResponse.culturalCommonsense);
      } else {
        console.log('No existing response, clearing form');
        setAnswer('');
        setCulturalCommonsense(null);
      }
    }
  }, [currentQuestionData?.questionId, lastQuestionId, state.responses]);

  useEffect(() => {
    if (textareaRef.current) {
      textareaRef.current.style.height = 'auto';
      textareaRef.current.style.height = textareaRef.current.scrollHeight + 'px';
    }
  }, [answer]);

  const validateForm = () => {
    const newErrors: { answer?: string; cultural?: string } = {};
    
    const answerValidation = validateAnswer(answer);
    if (!answerValidation.isValid) {
      newErrors.answer = answerValidation.message;
    }

    if (culturalCommonsense === null) {
      newErrors.cultural = 'Please select whether this pertains to cultural commonsense';
    }

    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleAnswerChange = (e: React.ChangeEvent<HTMLTextAreaElement>) => {
    setAnswer(e.target.value);
    if (errors.answer) {
      setErrors(prev => ({ ...prev, answer: undefined }));
    }
  };

  const handleCulturalChange = (value: boolean) => {
    setCulturalCommonsense(value);
    if (errors.cultural) {
      setErrors(prev => ({ ...prev, cultural: undefined }));
    }
  };

  const handleClear = () => {
    setAnswer('');
    setCulturalCommonsense(null);
    setErrors({});
    setShowSuccess(false);
    if (textareaRef.current) {
      textareaRef.current.focus();
    }
  };

  const checkForMilestones = () => {
    const { categoryIndex, subcategoryIndex, topicIndex, questionIndex } = state.currentPosition;
    const currentCategory = state.questionsData[categoryIndex];
    const currentSubcategory = currentCategory?.subcategories[subcategoryIndex];
    const currentTopic = currentSubcategory?.topics[topicIndex];
    
    if (!currentTopic) return null;

    // Check if this is the last question in the topic
    if (questionIndex === currentTopic.questions.length - 1) {
      // Check if this is also the last topic in subcategory
      if (topicIndex === currentSubcategory.topics.length - 1) {
        // Check if this is also the last subcategory in category
        if (subcategoryIndex === currentCategory.subcategories.length - 1) {
          return 'category';
        }
        return 'subcategory';
      }
      return 'topic';
    }
    return null;
  };

  const handleSave = async () => {
    if (!validateForm() || !currentQuestionData || !state.sessionId) return;

    setIsSaving(true);
    
    try {
      const timeSpent = Math.floor((Date.now() - startTime) / 1000);
      
      const response = {
        sessionId: state.sessionId,
        questionId: currentQuestionData.questionId,
        categoryIndex: state.currentPosition.categoryIndex,
        subcategoryIndex: state.currentPosition.subcategoryIndex,
        topicIndex: state.currentPosition.topicIndex,
        questionIndex: state.currentPosition.questionIndex,
        category: currentQuestionData.category,
        subcategory: currentQuestionData.subcategory,
        topic: currentQuestionData.topic,
        question: currentQuestionData.question,
        answer: answer.trim(),
        culturalCommonsense: culturalCommonsense!,
        timeSpent,
        timestamp: new Date().toISOString()
      };

      await saveResponse(response);
      setShowSuccess(true);
      setTimeout(() => setShowSuccess(false), 2000);
      
    } catch (error) {
      console.error('SAVE ERROR:', error);
      alert('Failed to save response: ' + (error as Error).message);
    } finally {
      setIsSaving(false);
    }
  };

  const handleNext = async () => {
    await handleSave();
    if (Object.keys(errors).length === 0 && answer.trim() && culturalCommonsense !== null) {
      // Check for milestone before navigating
      const milestone = checkForMilestones();
      
      setNavigationDirection('next');
      setIsNavigating(true);
      
      setTimeout(() => {
        navigateToNext();
        
        // Show celebration if milestone reached
        if (milestone) {
          setShowCelebration(milestone);
          setTimeout(() => setShowCelebration(null), 3000);
        }
      }, 300);
    }
  };

  const handlePrevious = () => {
    setNavigationDirection('previous');
    setIsNavigating(true);
    
    setTimeout(() => {
      navigateToPrevious();
    }, 300);
  };

  const handleSkip = () => {
    setAnswer('');
    setCulturalCommonsense(null);
    setErrors({});
    
    setNavigationDirection('next');
    setIsNavigating(true);
    
    setTimeout(() => {
      navigateToNext();
    }, 300);
  };

  if (showAttentionCheck && attentionCheck) {
    return <AttentionCheck attentionCheck={attentionCheck} onComplete={() => {}} />;
  }

  if (!currentQuestionData) {
    return (
      <div className="min-h-screen bg-gray-100 flex items-center justify-center">
        <div className="text-center">
          <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-blue-600 mx-auto mb-4"></div>
          <p className="text-gray-600">Loading question...</p>
        </div>
      </div>
    );
  }

  const topicProgress = getTotalQuestionsInCurrentTopic();
  const completedInTopic = getCompletedQuestionsInCurrentTopic();
  const currentQuestionInTopic = state.currentPosition.questionIndex + 1;
  const isFirstQuestion = state.currentPosition.categoryIndex === 0 && 
                         state.currentPosition.subcategoryIndex === 0 && 
                         state.currentPosition.topicIndex === 0 && 
                         state.currentPosition.questionIndex === 0;

  return (
    <div className="min-h-screen bg-gradient-to-br from-indigo-50 via-purple-50 to-pink-50">
      <ProgressBar />
      
      {/* Celebration Overlay */}
      {showCelebration && (
        <CelebrationOverlay type={showCelebration} currentQuestionData={currentQuestionData} />
      )}
      
      <div className="max-w-5xl mx-auto px-6 py-8">
        {/* Question Card */}
        <div className={`transform transition-all duration-500 ease-in-out ${
          isNavigating 
            ? navigationDirection === 'next' 
              ? 'translate-x-[-100%] opacity-0 scale-95' 
              : 'translate-x-[100%] opacity-0 scale-95'
            : 'translate-x-0 opacity-100 scale-100'
        }`}>
          
          {/* Topic Header Card */}
          <div className="bg-white/80 backdrop-blur-sm rounded-3xl shadow-2xl p-8 mb-8 border border-white/20 hover:shadow-3xl transition-all duration-300">
            <div className="text-center mb-6">
              <div className="inline-flex items-center px-6 py-3 bg-gradient-to-r from-blue-500 to-purple-600 text-white rounded-full text-sm font-medium mb-6 shadow-lg">
                <span className="mr-2">📋</span>
                Question {currentQuestionInTopic} of {topicProgress} in this topic
              </div>
              
              <h1 className="text-3xl md:text-4xl font-bold bg-gradient-to-r from-purple-600 to-pink-600 bg-clip-text text-transparent mb-4">
                {currentQuestionData.topic}
              </h1>
              
              <div className="flex items-center justify-center space-x-3 text-sm">
                <span className="px-4 py-2 bg-gradient-to-r from-blue-100 to-blue-200 text-blue-800 rounded-full font-medium shadow-sm">
                  {currentQuestionData.category}
                </span>
                <span className="text-gray-400">•</span>
                <span className="px-4 py-2 bg-gradient-to-r from-green-100 to-green-200 text-green-800 rounded-full font-medium shadow-sm">
                  {currentQuestionData.subcategory}
                </span>
              </div>
            </div>

            {/* Topic Progress Bar */}
            <div className="relative">
              <div className="w-full bg-gray-200 rounded-full h-3 overflow-hidden">
                <div 
                  className="bg-gradient-to-r from-purple-500 via-pink-500 to-orange-500 h-3 rounded-full transition-all duration-1000 ease-out relative"
                  style={{ width: `${(completedInTopic / topicProgress) * 100}%` }}
                >
                  <div className="absolute inset-0 bg-white opacity-30 animate-pulse"></div>
                </div>
              </div>
              
              <div className="text-center text-sm text-gray-600 mt-3 font-medium">
                {completedInTopic} of {topicProgress} questions completed in this topic
              </div>
            </div>
          </div>

          {/* Main Question Card */}
          <div className="bg-white/90 backdrop-blur-sm rounded-3xl shadow-2xl border border-white/20 overflow-hidden hover:shadow-3xl transition-all duration-300">
            
            {/* Question Header */}
            <div className="bg-gradient-to-r from-indigo-500 via-purple-500 to-pink-500 p-8 text-white">
              <h2 className="text-xl md:text-2xl font-semibold leading-relaxed">
                {currentQuestionData.question}
              </h2>
            </div>

            {/* Answer Section */}
            <div className="p-8">
              <div className="mb-8">
                <div className="flex justify-between items-center mb-4">
                  <label htmlFor="main-answer" className="block text-lg font-semibold text-gray-800">
                    Your Answer *
                  </label>
                  <button
                    onClick={handleClear}
                    className="text-sm text-indigo-600 hover:text-indigo-800 font-medium hover:underline transition-colors"
                  >
                    Clear Form
                  </button>
                </div>
                
                <div className="relative">
                  <textarea
                    ref={textareaRef}
                    id="main-answer"
                    value={answer}
                    onChange={handleAnswerChange}
                    placeholder="Share your knowledge about cultural practices in your region..."
                    className={`w-full px-6 py-4 border-2 rounded-2xl focus:ring-4 focus:ring-indigo-200 resize-none min-h-[150px] transition-all duration-200 font-medium text-gray-700 ${
                      errors.answer ? 'border-red-300 focus:border-red-500' : 'border-gray-200 focus:border-indigo-400'
                    }`}
                    maxLength={5000}
                    disabled={isSaving}
                  />
                  
                  {/* Character count badge */}
                  <div className="absolute bottom-4 right-4 px-3 py-1 bg-gray-100 rounded-full text-xs text-gray-500">
                    {answer.length}/5000
                  </div>
                </div>
                
                {errors.answer && (
                  <p className="text-sm text-red-600 mt-2 font-medium">{errors.answer}</p>
                )}
              </div>

              {/* Cultural Commonsense Section */}
              <div className="mb-8 p-6 bg-gradient-to-r from-blue-50 to-indigo-50 rounded-2xl border border-blue-100">
                <h3 className="font-bold text-indigo-900 mb-4 text-lg">
                  🧠 Cultural Commonsense Assessment
                </h3>
                <p className="text-sm text-indigo-800 mb-4 leading-relaxed">
                  Cultural commonsense refers to everyday beliefs, behaviors, values, and practices 
                  that are perceived as "natural" and widely shared within a cultural group.
                </p>
                <p className="font-semibold text-indigo-900 mb-4">
                  Does this question pertain to cultural commonsense in your region? *
                </p>
                
                <div className="space-y-3">
                  <div 
                    className="flex items-center cursor-pointer p-4 rounded-xl hover:bg-white/60 transition-colors"
                    onClick={() => handleCulturalChange(true)}
                  >
                    <input
                      type="radio"
                      id="cultural-yes"
                      checked={culturalCommonsense === true}
                      onChange={() => handleCulturalChange(true)}
                      disabled={isSaving}
                      className="h-5 w-5 text-indigo-600 focus:ring-indigo-500"
                    />
                    <label htmlFor="cultural-yes" className="ml-4 text-gray-800 font-medium cursor-pointer">
                      ✅ Yes, this relates to cultural commonsense
                    </label>
                  </div>
                  
                  <div 
                    className="flex items-center cursor-pointer p-4 rounded-xl hover:bg-white/60 transition-colors"
                    onClick={() => handleCulturalChange(false)}
                  >
                    <input
                      type="radio"
                      id="cultural-no"
                      checked={culturalCommonsense === false}
                      onChange={() => handleCulturalChange(false)}
                      disabled={isSaving}
                      className="h-5 w-5 text-indigo-600 focus:ring-indigo-500"
                    />
                    <label htmlFor="cultural-no" className="ml-4 text-gray-800 font-medium cursor-pointer">
                      ❌ No, this does not relate to cultural commonsense
                    </label>
                  </div>
                </div>
                
                {errors.cultural && (
                  <p className="text-sm text-red-600 mt-3 font-medium">{errors.cultural}</p>
                )}
              </div>

              {/* Success Message */}
              {showSuccess && (
                <div className="mb-6 p-4 bg-gradient-to-r from-green-50 to-emerald-50 border border-green-200 rounded-2xl animate-bounce-in">
                  <div className="flex items-center">
                    <span className="text-green-600 mr-3 text-xl">✨</span>
                    <span className="text-green-800 font-semibold">Response saved successfully!</span>
                  </div>
                </div>
              )}

              {/* Navigation Buttons */}
              <div className="flex justify-between items-center pt-6 border-t border-gray-200">
                <button
                  onClick={handlePrevious}
                  disabled={isFirstQuestion || isNavigating}
                  className="flex items-center px-6 py-3 bg-gray-100 hover:bg-gray-200 disabled:bg-gray-50 disabled:text-gray-400 text-gray-700 font-semibold rounded-2xl transition-all duration-200 disabled:cursor-not-allowed transform hover:scale-105 active:scale-95"
                >
                  <span className="mr-2">←</span>
                  Previous
                </button>

                <div className="flex space-x-4">
                  <button
                    onClick={handleSkip}
                    disabled={isNavigating}
                    className="px-6 py-3 bg-gradient-to-r from-yellow-400 to-orange-400 hover:from-yellow-500 hover:to-orange-500 text-white font-semibold rounded-2xl transition-all duration-200 transform hover:scale-105 active:scale-95 shadow-lg"
                  >
                    Skip Question
                  </button>

                  <button
                    onClick={handleSave}
                    disabled={isSaving || !answer.trim() || culturalCommonsense === null || isNavigating}
                    className="px-6 py-3 bg-gradient-to-r from-blue-500 to-indigo-500 hover:from-blue-600 hover:to-indigo-600 disabled:from-gray-400 disabled:to-gray-500 text-white font-semibold rounded-2xl transition-all duration-200 disabled:cursor-not-allowed transform hover:scale-105 active:scale-95 shadow-lg"
                  >
                    {isSaving ? (
                      <div className="flex items-center">
                        <div className="animate-spin rounded-full h-5 w-5 border-b-2 border-white mr-2"></div>
                        Saving...
                      </div>
                    ) : (
                      'Save'
                    )}
                  </button>

                  <button
                    onClick={handleNext}
                    disabled={isSaving || !answer.trim() || culturalCommonsense === null || isNavigating}
                    className="flex items-center px-8 py-3 bg-gradient-to-r from-purple-600 to-pink-600 hover:from-purple-700 hover:to-pink-700 disabled:from-gray-400 disabled:to-gray-500 text-white font-bold rounded-2xl transition-all duration-200 disabled:cursor-not-allowed transform hover:scale-105 active:scale-95 shadow-lg"
                  >
                    {isNavigating ? (
                      <div className="flex items-center">
                        <div className="animate-spin rounded-full h-5 w-5 border-b-2 border-white mr-2"></div>
                        Moving...
                      </div>
                    ) : (
                      <>
                        Save & Continue
                        <span className="ml-2">→</span>
                      </>
                    )}
                  </button>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

// Celebration Component
function CelebrationOverlay({ type, currentQuestionData }: { type: string, currentQuestionData: any }) {
  const celebrations = {
    topic: {
      icon: '🎯',
      title: 'Topic Completed!',
      message: `Great work finishing "${currentQuestionData.topic}"!`,
      confetti: '🎊🌟✨',
      gradient: 'from-green-400 to-blue-500'
    },
    subcategory: {
      icon: '🏆',
      title: 'Subcategory Mastered!',
      message: `Excellent! You've completed "${currentQuestionData.subcategory}"!`,
      confetti: '🎉🎊🌟💫',
      gradient: 'from-purple-400 to-pink-500'
    },
    category: {
      icon: '👑',
      title: 'Category Champion!',
      message: `Amazing! You've conquered the entire "${currentQuestionData.category}" category!`,
      confetti: '🎉🎊🌟💫✨🎯',
      gradient: 'from-yellow-400 via-red-500 to-purple-600'
    }
  };

  const celebration = celebrations[type as keyof typeof celebrations];

  return (
    <div className="fixed inset-0 bg-black/50 backdrop-blur-sm flex items-center justify-center z-50 animate-fade-in">
      <div className="bg-white rounded-3xl p-8 max-w-md mx-4 text-center transform animate-bounce-in shadow-2xl">
        <div className="text-6xl mb-4 animate-pulse">{celebration.icon}</div>
        <h2 className={`text-3xl font-bold bg-gradient-to-r ${celebration.gradient} bg-clip-text text-transparent mb-4`}>
          {celebration.title}
        </h2>
        <p className="text-gray-700 text-lg mb-6">{celebration.message}</p>
        <div className="text-4xl animate-bounce">
          {celebration.confetti}
        </div>
      </div>
    </div>
  );
}
