import React from 'react';
import { useForm } from '../context/FormContext.tsx';

export default function ProgressBar() {
  const { 
    state, 
    calculateProgress, 
    getTotalQuestionsInCurrentTopic, 
    getCompletedQuestionsInCurrentTopic 
  } = useForm();

  if (!state.questionsData || state.questionsData.length === 0) {
    return (
      <div className="bg-white/80 backdrop-blur-sm px-6 py-3 sticky top-0 z-10 shadow-sm" style={{
        borderBottom: '1px solid rgba(139, 69, 255, 0.08)'
      }}>
        <div className="max-w-4xl mx-auto">
          <div className="animate-pulse">
            <div className="h-3 rounded mb-2" style={{ backgroundColor: '#e0e7ff' }}></div>
            <div className="h-2 rounded" style={{ backgroundColor: '#dbeafe' }}></div>
          </div>
        </div>
      </div>
    );
  }

  const overallProgress = calculateProgress();
  const totalInTopic = getTotalQuestionsInCurrentTopic();
  const completedInTopic = getCompletedQuestionsInCurrentTopic();
  const topicProgress = totalInTopic > 0 ? (completedInTopic / totalInTopic) * 100 : 0;

  const { categoryIndex, subcategoryIndex, topicIndex, questionIndex } = state.currentPosition;
  
  const currentCategory = state.questionsData[categoryIndex] || null;
  const currentSubcategory = currentCategory?.subcategories[subcategoryIndex] || null;
  const currentTopic = currentSubcategory?.topics[topicIndex] || null;

  if (!currentCategory || !currentSubcategory || !currentTopic) {
    return (
      <div className="bg-white/80 backdrop-blur-sm px-6 py-3 sticky top-0 z-10 shadow-sm" style={{
        borderBottom: '1px solid rgba(139, 69, 255, 0.08)'
      }}>
        <div className="max-w-4xl mx-auto">
          <div className="animate-pulse">
            <div className="h-3 rounded mb-2" style={{ backgroundColor: '#e0e7ff' }}></div>
            <div className="h-2 rounded" style={{ backgroundColor: '#dbeafe' }}></div>
          </div>
        </div>
      </div>
    );
  }

  const currentQuestionNumber = questionIndex + 1;
  const totalQuestionsInTopic = currentTopic.questions.length;

  return (
    <div className="bg-white/80 backdrop-blur-sm px-6 py-3 sticky top-0 z-10 shadow-sm" style={{
      borderBottom: '1px solid rgba(139, 69, 255, 0.08)'
    }}>
      <div className="max-w-4xl mx-auto">
        {/* Breadcrumb Navigation */}
        <div className="text-sm mb-2 flex flex-wrap items-center">
          <span className="font-medium" style={{ color: '#059669' }}>
            {currentCategory.category}
          </span>
          <span className="mx-2" style={{ color: '#d8b4fe' }}>→</span>
          <span className="font-medium" style={{ color: '#d97706' }}>
            {currentSubcategory.subcategory}
          </span>
          <span className="mx-2" style={{ color: '#d8b4fe' }}>→</span>
          <span className="font-medium" style={{ color: '#6366f1' }}>
            {currentTopic.topic}
          </span>
          <span className="ml-auto text-xs px-2 py-1 rounded-full font-medium" style={{
            backgroundColor: '#f3e8ff',
            color: '#6b46c1',
            border: '1px solid #e9d5ff'
          }}>
            Question {currentQuestionNumber} of {totalQuestionsInTopic}
          </span>
        </div>

        {/* Overall Survey Progress */}
        <div>
          <div className="flex items-center justify-between text-sm mb-1">
            <span className="font-medium" style={{ color: '#4338ca' }}>
              Overall Progress
            </span>
            <span className="flex items-center text-xs" style={{ color: '#6366f1' }}>
              <span className="font-semibold" style={{ color: '#0891b2' }}>{state.responses.size}</span>
              <span className="mx-1">/</span>
              <span>{state.progress.totalQuestions}</span>
              <span className="ml-1">questions</span>
            </span>
          </div>
          <div className="w-full rounded-full h-2 overflow-hidden" style={{ backgroundColor: '#e0e7ff' }}>
            <div 
              className="h-2 rounded-full transition-all duration-700 ease-out"
              style={{
                background: 'linear-gradient(90deg, #6366f1 0%, #8b5cf6 50%, #ec4899 100%)',
                width: `${Math.min(overallProgress, 100)}%`
              }}
            ></div>
          </div>
          <div className="flex justify-between items-center text-xs mt-1" style={{ color: '#6366f1' }}>
            <span>{overallProgress.toFixed(1)}% complete</span>
            <span>
              {state.progress.totalQuestions - state.responses.size} remaining
            </span>
          </div>
        </div>
      </div>
    </div>
  );
}
