import React, { useState, useEffect, useRef } from 'react';
import { useForm } from '../context/FormContext.tsx';
import { validateAnswer } from '../utils/helpers.ts';
import AttentionCheck from './AttentionCheck.tsx';
import ProgressBar from './ProgressBar.tsx';

export default function QuestionForm() {
  const {
    state,
    saveResponse,
    navigateToNext,
    navigateToPrevious,
    getCurrentQuestionData,
    getTotalQuestionsInCurrentTopic,
    getCompletedQuestionsInCurrentTopic,
    resetSession
  } = useForm();

  const [answer, setAnswer] = useState('');
  const [culturalCommonsense, setCulturalCommonsense] = useState<boolean | null>(null);
  const [errors, setErrors] = useState<{ answer?: string; cultural?: string }>({});
  const [isSaving, setIsSaving] = useState(false);
  const [showAttentionCheck, setShowAttentionCheck] = useState(false);
  const [attentionCheck, setAttentionCheck] = useState<any>(null);
  const [startTime, setStartTime] = useState(Date.now());
  const [showSuccess, setShowSuccess] = useState(false);
  const [lastQuestionId, setLastQuestionId] = useState<string | null>(null);
  const [isNavigating, setIsNavigating] = useState(false);
  const [navigationDirection, setNavigationDirection] = useState<'next' | 'previous' | null>(null);
  const [showCelebration, setShowCelebration] = useState<{type: string, data: any} | null>(null);
  const [completedTopicName, setCompletedTopicName] = useState<string>('');
  const textareaRef = useRef<HTMLTextAreaElement>(null);

  const currentQuestionData = getCurrentQuestionData();

  // Reset form when question changes
  useEffect(() => {
    if (currentQuestionData && currentQuestionData.questionId !== lastQuestionId) {
      console.log('Question changed, loading data for:', currentQuestionData.questionId);
      
      // Reset form state
      setErrors({});
      setShowSuccess(false);
      setStartTime(Date.now());
      setLastQuestionId(currentQuestionData.questionId);
      setIsNavigating(false);
      setNavigationDirection(null);
      
      // Load existing response or reset
      const existingResponse = state.responses.get(currentQuestionData.questionId);
      if (existingResponse) {
        console.log('Found existing response:', existingResponse);
        setAnswer(existingResponse.answer);
        setCulturalCommonsense(existingResponse.culturalCommonsense);
      } else {
        console.log('No existing response, clearing form');
        setAnswer('');
        setCulturalCommonsense(null);
      }
    }
  }, [currentQuestionData?.questionId, lastQuestionId, state.responses]);

  useEffect(() => {
    if (textareaRef.current) {
      textareaRef.current.style.height = 'auto';
      textareaRef.current.style.height = textareaRef.current.scrollHeight + 'px';
    }
  }, [answer]);

  const validateForm = () => {
    const newErrors: { answer?: string; cultural?: string } = {};
    
    const answerValidation = validateAnswer(answer);
    if (!answerValidation.isValid) {
      newErrors.answer = answerValidation.message;
    }

    if (culturalCommonsense === null) {
      newErrors.cultural = 'Please select whether this pertains to cultural commonsense';
    }

    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleAnswerChange = (e: React.ChangeEvent<HTMLTextAreaElement>) => {
    setAnswer(e.target.value);
    if (errors.answer) {
      setErrors(prev => ({ ...prev, answer: undefined }));
    }
  };

  const handleCulturalChange = (value: boolean) => {
    setCulturalCommonsense(value);
    if (errors.cultural) {
      setErrors(prev => ({ ...prev, cultural: undefined }));
    }
  };

  const handleClear = () => {
    setAnswer('');
    setCulturalCommonsense(null);
    setErrors({});
    setShowSuccess(false);
    if (textareaRef.current) {
      textareaRef.current.focus();
    }
  };

  const checkForMilestones = () => {
    const { categoryIndex, subcategoryIndex, topicIndex, questionIndex } = state.currentPosition;
    const currentCategory = state.questionsData[categoryIndex];
    const currentSubcategory = currentCategory?.subcategories[subcategoryIndex];
    const currentTopic = currentSubcategory?.topics[topicIndex];
    
    if (!currentTopic) return null;

    // Store the current topic name for celebration
    setCompletedTopicName(currentTopic.topic);

    // Check if this is the last question in the topic
    if (questionIndex === currentTopic.questions.length - 1) {
      // Check if this is also the last topic in subcategory
      if (topicIndex === currentSubcategory.topics.length - 1) {
        // Check if this is also the last subcategory in category
        if (subcategoryIndex === currentCategory.subcategories.length - 1) {
          return {
            type: 'category',
            name: currentCategory.category,
            subcategoryName: currentSubcategory.subcategory,
            topicName: currentTopic.topic
          };
        }
        return {
          type: 'subcategory',
          name: currentSubcategory.subcategory,
          topicName: currentTopic.topic,
          categoryName: currentCategory.category
        };
      }
      return {
        type: 'topic',
        name: currentTopic.topic,
        subcategoryName: currentSubcategory.subcategory,
        categoryName: currentCategory.category
      };
    }
    return null;
  };

  const handleSave = async () => {
    if (!validateForm() || !currentQuestionData || !state.sessionId) return;

    setIsSaving(true);
    
    try {
      const timeSpent = Math.floor((Date.now() - startTime) / 1000);
      
      const response = {
        sessionId: state.sessionId,
        questionId: currentQuestionData.questionId,
        categoryIndex: state.currentPosition.categoryIndex,
        subcategoryIndex: state.currentPosition.subcategoryIndex,
        topicIndex: state.currentPosition.topicIndex,
        questionIndex: state.currentPosition.questionIndex,
        category: currentQuestionData.category,
        subcategory: currentQuestionData.subcategory,
        topic: currentQuestionData.topic,
        question: currentQuestionData.question,
        answer: answer.trim(),
        culturalCommonsense: culturalCommonsense!,
        timeSpent,
        timestamp: new Date().toISOString()
      };

      await saveResponse(response);
      setShowSuccess(true);
      setTimeout(() => setShowSuccess(false), 2000);
      
    } catch (error) {
      console.error('SAVE ERROR:', error);
      alert('Failed to save response: ' + (error as Error).message);
    } finally {
      setIsSaving(false);
    }
  };

  const handleNext = async () => {
    await handleSave();
    if (Object.keys(errors).length === 0 && answer.trim() && culturalCommonsense !== null) {
      // Check for milestone before navigating
      const milestone = checkForMilestones();
      
      setNavigationDirection('next');
      setIsNavigating(true);
      
      setTimeout(() => {
        navigateToNext();
        
        // Show celebration if milestone reached
        if (milestone) {
          setShowCelebration({type: milestone.type, data: milestone});
        }
      }, 300);
    }
  };

  const handlePrevious = () => {
    setNavigationDirection('previous');
    setIsNavigating(true);
    
    setTimeout(() => {
      navigateToPrevious();
    }, 300);
  };

  const handleSkip = () => {
    setAnswer('');
    setCulturalCommonsense(null);
    setErrors({});
    
    setNavigationDirection('next');
    setIsNavigating(true);
    
    setTimeout(() => {
      navigateToNext();
    }, 300);
  };

  const closeCelebration = () => {
    setShowCelebration(null);
  };

  if (showAttentionCheck && attentionCheck) {
    return <AttentionCheck attentionCheck={attentionCheck} onComplete={() => {}} />;
  }

  if (!currentQuestionData) {
    return (
      <div className="min-h-screen bg-gray-100 flex items-center justify-center">
        <div className="text-center">
          <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-blue-600 mx-auto mb-4"></div>
          <p className="text-gray-600">Loading question...</p>
        </div>
      </div>
    );
  }

  const topicProgress = getTotalQuestionsInCurrentTopic();
  const completedInTopic = getCompletedQuestionsInCurrentTopic();
  const currentQuestionInTopic = state.currentPosition.questionIndex + 1;
  const isFirstQuestion = state.currentPosition.categoryIndex === 0 && 
                         state.currentPosition.subcategoryIndex === 0 && 
                         state.currentPosition.topicIndex === 0 && 
                         state.currentPosition.questionIndex === 0;

  return (
    <div className="min-h-screen bg-gradient-to-br from-slate-50 via-blue-50 to-indigo-50">
      <ProgressBar />
      
      {/* Celebration Modal */}
      {showCelebration && (
        <CelebrationModal 
          celebration={showCelebration} 
          onClose={closeCelebration}
        />
      )}
      
      <div className="max-w-4xl mx-auto px-4 py-6">
        {/* Question Card Container */}
        <div className="relative overflow-hidden">
          <div className={`transform transition-all duration-500 ease-in-out ${
            isNavigating 
              ? navigationDirection === 'next' 
                ? '-translate-x-full opacity-0'
                : 'translate-x-full opacity-0'
              : 'translate-x-0 opacity-100'
          }`}>
            
            {/* Topic Header Card */}
            <div className="bg-white/70 backdrop-blur-sm rounded-2xl shadow-lg p-6 mb-6 border border-slate-200/50">
              <div className="text-center mb-4">
                <div className="inline-flex items-center px-4 py-2 bg-gradient-to-r from-slate-400 to-slate-500 text-white rounded-full text-sm font-medium mb-4 shadow-sm">
                  <span className="mr-2">📝</span>
                  Question {currentQuestionInTopic} of {topicProgress}
                </div>
                
                <h1 className="text-2xl md:text-3xl font-semibold text-slate-700 mb-3">
                  {currentQuestionData.topic}
                </h1>
                
                <div className="flex items-center justify-center space-x-2 text-sm">
                  <span className="px-3 py-1 bg-blue-100/70 text-blue-700 rounded-full font-medium">
                    {currentQuestionData.category}
                  </span>
                  <span className="text-slate-400">•</span>
                  <span className="px-3 py-1 bg-green-100/70 text-green-700 rounded-full font-medium">
                    {currentQuestionData.subcategory}
                  </span>
                </div>
              </div>

              {/* Topic Progress Bar */}
              <div className="relative">
                <div className="w-full bg-slate-200 rounded-full h-2 overflow-hidden">
                  <div 
                    className="bg-gradient-to-r from-blue-300 to-indigo-400 h-2 rounded-full transition-all duration-1000 ease-out"
                    style={{ width: `${(completedInTopic / topicProgress) * 100}%` }}
                  ></div>
                </div>
                
                <div className="text-center text-sm text-slate-600 mt-2">
                  {completedInTopic} of {topicProgress} questions completed
                </div>
              </div>
            </div>

            {/* Main Question Card */}
            <div className="bg-white/80 backdrop-blur-sm rounded-2xl shadow-lg border border-slate-200/50 overflow-hidden">
              
              {/* Question Header */}
              <div className="bg-gradient-to-r from-slate-400 to-slate-500 p-6 text-white">
                <h2 className="text-lg md:text-xl font-medium leading-relaxed">
                  {currentQuestionData.question}
                </h2>
              </div>

              {/* Answer Section */}
              <div className="p-6">
                <div className="mb-6">
                  <div className="flex justify-between items-center mb-3">
                    <label htmlFor="main-answer" className="block text-base font-medium text-slate-700">
                      Your Answer *
                    </label>
                    <button
                      onClick={handleClear}
                      className="text-sm text-slate-500 hover:text-slate-700 font-medium hover:underline transition-colors"
                    >
                      Clear Form
                    </button>
                  </div>
                  
                  <div className="relative">
                    <textarea
                      ref={textareaRef}
                      id="main-answer"
                      value={answer}
                      onChange={handleAnswerChange}
                      placeholder="Share your knowledge about cultural practices in your region..."
                      className={`w-full px-4 py-3 border-2 rounded-xl focus:ring-2 focus:ring-slate-300 resize-none min-h-[120px] transition-all duration-200 text-slate-700 ${
                        errors.answer ? 'border-red-300 focus:border-red-400' : 'border-slate-200 focus:border-slate-400'
                      }`}
                      maxLength={5000}
                      disabled={isSaving}
                    />
                    
                    {/* Character count badge */}
                    <div className="absolute bottom-3 right-3 px-2 py-1 bg-slate-100 rounded-lg text-xs text-slate-500">
                      {answer.length}/5000
                    </div>
                  </div>
                  
                  {errors.answer && (
                    <p className="text-sm text-red-500 mt-2">{errors.answer}</p>
                  )}
                </div>

                {/* Cultural Commonsense Section */}
                <div className="mb-6 p-4 bg-gradient-to-r from-blue-50/50 to-indigo-50/50 rounded-xl border border-blue-100/50">
                  <h3 className="font-medium text-slate-700 mb-3 text-base">
                    🧠 Cultural Commonsense Assessment
                  </h3>
                  <p className="text-sm text-slate-600 mb-3 leading-relaxed">
                    Cultural commonsense refers to everyday beliefs, behaviors, values, and practices 
                    that are perceived as "natural" and widely shared within a cultural group.
                  </p>
                  <p className="font-medium text-slate-700 mb-3 text-sm">
                    Does this question pertain to cultural commonsense in your region? *
                  </p>
                  
                  <div className="space-y-2">
                    <div 
                      className="flex items-center cursor-pointer p-3 rounded-lg hover:bg-white/60 transition-colors"
                      onClick={() => handleCulturalChange(true)}
                    >
                      <input
                        type="radio"
                        id="cultural-yes"
                        checked={culturalCommonsense === true}
                        onChange={() => handleCulturalChange(true)}
                        disabled={isSaving}
                        className="h-4 w-4 text-slate-500 focus:ring-slate-300"
                      />
                      <label htmlFor="cultural-yes" className="ml-3 text-slate-700 cursor-pointer text-sm">
                        ✅ Yes, this relates to cultural commonsense
                      </label>
                    </div>
                    
                    <div 
                      className="flex items-center cursor-pointer p-3 rounded-lg hover:bg-white/60 transition-colors"
                      onClick={() => handleCulturalChange(false)}
                    >
                      <input
                        type="radio"
                        id="cultural-no"
                        checked={culturalCommonsense === false}
                        onChange={() => handleCulturalChange(false)}
                        disabled={isSaving}
                        className="h-4 w-4 text-slate-500 focus:ring-slate-300"
                      />
                      <label htmlFor="cultural-no" className="ml-3 text-slate-700 cursor-pointer text-sm">
                        ❌ No, this does not relate to cultural commonsense
                      </label>
                    </div>
                  </div>
                  
                  {errors.cultural && (
                    <p className="text-sm text-red-500 mt-2">{errors.cultural}</p>
                  )}
                </div>

                {/* Success Message */}
                {showSuccess && (
                  <div className="mb-4 p-3 bg-gradient-to-r from-green-50 to-emerald-50 border border-green-200/50 rounded-xl animate-bounce-in">
                    <div className="flex items-center">
                      <span className="text-green-600 mr-2">✨</span>
                      <span className="text-green-700 font-medium text-sm">Response saved successfully!</span>
                    </div>
                  </div>
                )}

                {/* Navigation Buttons */}
                <div className="flex justify-between items-center pt-4 border-t border-slate-200">
                  <button
                    onClick={handlePrevious}
                    disabled={isFirstQuestion || isNavigating}
                    className="flex items-center px-4 py-2 bg-slate-100 hover:bg-slate-200 disabled:bg-slate-50 disabled:text-slate-400 text-slate-600 font-medium rounded-xl transition-all duration-200 disabled:cursor-not-allowed text-sm"
                  >
                    <span className="mr-2">←</span>
                    Previous
                  </button>

                  <div className="flex space-x-3">
                    <button
                      onClick={handleSkip}
                      disabled={isNavigating}
                      className="px-4 py-2 bg-gradient-to-r from-yellow-200 to-orange-200 hover:from-yellow-300 hover:to-orange-300 text-orange-700 font-medium rounded-xl transition-all duration-200 text-sm"
                    >
                      Skip
                    </button>

                    <button
                      onClick={handleSave}
                      disabled={isSaving || !answer.trim() || culturalCommonsense === null || isNavigating}
                      className="px-4 py-2 bg-gradient-to-r from-blue-200 to-indigo-200 hover:from-blue-300 hover:to-indigo-300 disabled:from-slate-200 disabled:to-slate-300 text-slate-700 disabled:text-slate-500 font-medium rounded-xl transition-all duration-200 disabled:cursor-not-allowed text-sm"
                    >
                      {isSaving ? 'Saving...' : 'Save'}
                    </button>

                    <button
                      onClick={handleNext}
                      disabled={isSaving || !answer.trim() || culturalCommonsense === null || isNavigating}
                      className="flex items-center px-5 py-2 bg-gradient-to-r from-slate-300 to-slate-400 hover:from-slate-400 hover:to-slate-500 disabled:from-slate-200 disabled:to-slate-300 text-white disabled:text-slate-500 font-medium rounded-xl transition-all duration-200 disabled:cursor-not-allowed text-sm"
                    >
                      {isNavigating ? 'Moving...' : (
                        <>
                          Save & Continue
                          <span className="ml-2">→</span>
                        </>
                      )}
                    </button>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

// Enhanced Celebration Modal Component
function CelebrationModal({ celebration, onClose }: { 
  celebration: {type: string, data: any}, 
  onClose: () => void 
}) {
  const celebrations = {
    topic: {
      icon: '🎯',
      title: 'Topic Completed!',
      emoji: '🎊🌟✨',
      gradient: 'from-green-200 to-blue-200',
      textColor: 'text-green-700'
    },
    subcategory: {
      icon: '🏆',
      title: 'Subcategory Mastered!',
      emoji: '🎉🎊🌟💫',
      gradient: 'from-purple-200 to-pink-200',
      textColor: 'text-purple-700'
    },
    category: {
      icon: '👑',
      title: 'Category Champion!',
      emoji: '🎉🎊🌟💫✨🎯',
      gradient: 'from-yellow-200 via-orange-200 to-red-200',
      textColor: 'text-orange-700'
    }
  };

  const config = celebrations[celebration.type as keyof typeof celebrations];

  return (
    <div className="fixed inset-0 bg-black/30 backdrop-blur-sm flex items-center justify-center z-50 animate-fade-in p-4">
      <div className="bg-white rounded-2xl p-6 max-w-lg mx-auto text-center shadow-2xl animate-bounce-in">
        <div className="text-5xl mb-3">{config.icon}</div>
        <h2 className={`text-2xl font-bold ${config.textColor} mb-3`}>
          {config.title}
        </h2>
        <div className={`p-4 bg-gradient-to-r ${config.gradient} rounded-xl mb-4`}>
          <p className="text-slate-700 font-medium text-lg mb-2">
            "{celebration.data.name}"
          </p>
          <p className="text-slate-600 text-sm">
            {celebration.type === 'topic' && `in ${celebration.data.subcategoryName}`}
            {celebration.type === 'subcategory' && `from ${celebration.data.categoryName}`}
            {celebration.type === 'category' && 'Entire category completed!'}
          </p>
        </div>
        <div className="text-3xl mb-4 animate-pulse">
          {config.emoji}
        </div>
        <button
          onClick={onClose}
          className="px-6 py-2 bg-gradient-to-r from-slate-300 to-slate-400 hover:from-slate-400 hover:to-slate-500 text-white font-medium rounded-xl transition-all duration-200"
        >
          Continue Survey
        </button>
      </div>
    </div>
  );
}
