import React, { useState, useEffect, useRef } from 'react';
import { useForm } from '../context/FormContext.tsx';
import { validateAnswer, shouldShowAttentionCheck, generateAttentionCheck } from '../utils/helpers.ts';
import AttentionCheck from './AttentionCheck.tsx';
import ProgressBar from './ProgressBar.tsx';

export default function QuestionForm() {
  const {
  state,
  saveResponse,
  navigateToNext,
  navigateToPrevious,
  getCurrentQuestionData,
  getTotalQuestionsInCurrentTopic,
  getCompletedQuestionsInCurrentTopic,
  resetSession // Add this line
} = useForm();

  const [answer, setAnswer] = useState('');
  const [culturalCommonsense, setCulturalCommonsense] = useState<boolean | null>(null);
  const [errors, setErrors] = useState<{ answer?: string; cultural?: string }>({});
  const [isSaving, setIsSaving] = useState(false);
  const [showAttentionCheck, setShowAttentionCheck] = useState(false);
  const [attentionCheck, setAttentionCheck] = useState<any>(null);
  const [startTime, setStartTime] = useState(Date.now());
  const [showSuccess, setShowSuccess] = useState(false);
  const [lastQuestionId, setLastQuestionId] = useState<string | null>(null);
  const textareaRef = useRef<HTMLTextAreaElement>(null);

  const currentQuestionData = getCurrentQuestionData();

  // Debug logging
  console.log('QuestionForm render:', {
    currentQuestionData,
    answer,
    culturalCommonsense,
    questionsDataLength: state.questionsData.length,
    currentPosition: state.currentPosition,
    lastQuestionId,
    currentQuestionId: currentQuestionData?.questionId
  });

  // Reset form when question changes
  useEffect(() => {
    if (currentQuestionData && currentQuestionData.questionId !== lastQuestionId) {
      console.log('Question changed, loading data for:', currentQuestionData.questionId);
      
      // Reset form state
      setErrors({});
      setShowSuccess(false);
      setStartTime(Date.now());
      setLastQuestionId(currentQuestionData.questionId);
      
      // Load existing response or reset
      const existingResponse = state.responses.get(currentQuestionData.questionId);
      if (existingResponse) {
        console.log('Found existing response:', existingResponse);
        setAnswer(existingResponse.answer);
        setCulturalCommonsense(existingResponse.culturalCommonsense);
      } else {
        console.log('No existing response, clearing form');
        setAnswer('');
        setCulturalCommonsense(null);
      }
    }
  }, [currentQuestionData?.questionId, lastQuestionId, state.responses]);

  useEffect(() => {
    // Auto-resize textarea
    if (textareaRef.current) {
      textareaRef.current.style.height = 'auto';
      textareaRef.current.style.height = textareaRef.current.scrollHeight + 'px';
    }
  }, [answer]);

  const validateForm = () => {
    const newErrors: { answer?: string; cultural?: string } = {};
    
    const answerValidation = validateAnswer(answer);
    if (!answerValidation.isValid) {
      newErrors.answer = answerValidation.message;
    }

    if (culturalCommonsense === null) {
      newErrors.cultural = 'Please select whether this pertains to cultural commonsense';
    }

    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleAnswerChange = (e: React.ChangeEvent<HTMLTextAreaElement>) => {
    console.log('Answer changing to:', e.target.value);
    setAnswer(e.target.value);
    if (errors.answer) {
      setErrors(prev => ({ ...prev, answer: undefined }));
    }
  };

  const handleCulturalChange = (value: boolean) => {
    console.log('Cultural commonsense changing to:', value);
    setCulturalCommonsense(value);
    if (errors.cultural) {
      setErrors(prev => ({ ...prev, cultural: undefined }));
    }
  };

  const handleClear = () => {
    console.log('Clearing form');
    setAnswer('');
    setCulturalCommonsense(null);
    setErrors({});
    setShowSuccess(false);
    if (textareaRef.current) {
      textareaRef.current.focus();
    }
  };

  const handleSave = async () => {
    console.log('=== SAVE ATTEMPT ===');
    console.log('Current answer:', answer);
    console.log('Current cultural commonsense:', culturalCommonsense);
    console.log('Answer length:', answer.length);
    console.log('Answer trimmed length:', answer.trim().length);
    
    const validation = validateForm();
    console.log('Form validation result:', validation);
    console.log('Current errors:', errors);
    
    if (!validation || !currentQuestionData || !state.sessionId) {
      console.log('SAVE BLOCKED - Validation failed or missing data:', {
        isValid: validation,
        hasQuestionData: !!currentQuestionData,
        hasSessionId: !!state.sessionId,
        sessionId: state.sessionId
      });
      
      // Show specific error messages
      if (!state.sessionId) {
        alert('Session error. Please refresh the page and start over.');
      }
      return;
    }

    console.log('SAVE PROCEEDING - All validations passed');
    setIsSaving(true);
    
    try {
      const timeSpent = Math.floor((Date.now() - startTime) / 1000);
      
      const response = {
        sessionId: state.sessionId,
        questionId: currentQuestionData.questionId,
        categoryIndex: state.currentPosition.categoryIndex,
        subcategoryIndex: state.currentPosition.subcategoryIndex,
        topicIndex: state.currentPosition.topicIndex,
        questionIndex: state.currentPosition.questionIndex,
        category: currentQuestionData.category,
        subcategory: currentQuestionData.subcategory,
        topic: currentQuestionData.topic,
        question: currentQuestionData.question,
        answer: answer.trim(),
        culturalCommonsense: culturalCommonsense!,
        timeSpent,
        timestamp: new Date().toISOString()
      };

      console.log('SAVING RESPONSE:', response);
      
      await saveResponse(response);
      
      console.log('SAVE SUCCESSFUL');
      setShowSuccess(true);
      setTimeout(() => setShowSuccess(false), 2000);
      
    } catch (error) {
      console.error('SAVE ERROR:', error);
      alert('Failed to save response: ' + (error as Error).message);
    } finally {
      console.log('SAVE COMPLETE - Setting isSaving to false');
      setIsSaving(false);
    }
  };

  const handleNext = async () => {
    console.log('Next button clicked');
    await handleSave();
    // Only navigate if save was successful (no errors)
    if (Object.keys(errors).length === 0 && answer.trim() && culturalCommonsense !== null) {
      console.log('Navigating to next question');
      navigateToNext();
    }
  };

  const handlePrevious = () => {
    console.log('Previous button clicked');
    navigateToPrevious();
  };

  const handleSkip = () => {
    console.log('Skip button clicked');
    // Clear form and move to next
    setAnswer('');
    setCulturalCommonsense(null);
    setErrors({});
    navigateToNext();
  };

  const handleAttentionCheckComplete = (correct: boolean) => {
    setShowAttentionCheck(false);
    setAttentionCheck(null);
  };

  if (showAttentionCheck && attentionCheck) {
    return <AttentionCheck attentionCheck={attentionCheck} onComplete={handleAttentionCheckComplete} />;
  }

  if (!currentQuestionData) {
    return (
      <div className="min-h-screen bg-gray-100 flex items-center justify-center">
        <div className="text-center">
          <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-blue-600 mx-auto mb-4"></div>
          <p className="text-gray-600">Loading question...</p>
          <p className="text-sm text-gray-500 mt-2">
            Questions data length: {state.questionsData.length}
          </p>
          <p className="text-sm text-gray-500">
            Current position: {JSON.stringify(state.currentPosition)}
          </p>
        </div>
      </div>
    );
  }

  const topicProgress = getTotalQuestionsInCurrentTopic();
  const completedInTopic = getCompletedQuestionsInCurrentTopic();
  const currentQuestionInTopic = state.currentPosition.questionIndex + 1;
  const isFirstQuestion = state.currentPosition.categoryIndex === 0 && 
                         state.currentPosition.subcategoryIndex === 0 && 
                         state.currentPosition.topicIndex === 0 && 
                         state.currentPosition.questionIndex === 0;

  return (
    <div className="min-h-screen bg-gray-50">
      <ProgressBar />
      
      <div className="max-w-4xl mx-auto px-6 py-8">
        {/* Debug Panel */}
        <div className="bg-red-50 border border-red-200 rounded-lg p-4 mb-4">
          <div className="flex justify-between items-start">
            <div>
              <h3 className="font-bold text-red-800 mb-2">Debug Info:</h3>
              <div className="text-sm text-red-700 space-y-1">
                <p>Answer: "{answer}" (length: {answer.length})</p>
                <p>Cultural Commonsense: {culturalCommonsense?.toString() || 'null'}</p>
                <p>Is saving: {isSaving ? 'Yes' : 'No'}</p>
                <p>Question ID: {currentQuestionData.questionId}</p>
                <p>Session ID: {state.sessionId || 'Missing!'}</p>
                <p>Has existing response: {state.responses.has(currentQuestionData.questionId) ? 'Yes' : 'No'}</p>
              </div>
            </div>
            <button
              onClick={() => {
                if (confirm('Reset entire session? This will clear all progress!')) {
                  resetSession();
                  window.location.reload();
                }
              }}
              className="bg-red-600 hover:bg-red-700 text-white px-3 py-1 rounded text-sm"
            >
              Reset Session
            </button>
          </div>
        </div>

        {/* Topic Header */}
        <div className="bg-white rounded-2xl shadow-lg p-8 mb-8">
          <div className="text-center mb-6">
            <div className="inline-flex items-center px-4 py-2 bg-blue-100 rounded-full text-blue-800 text-sm font-medium mb-4">
              Question {currentQuestionInTopic} of {topicProgress} in this topic
            </div>
            
            <h1 className="text-2xl md:text-3xl font-bold text-gray-800 mb-2">
              {currentQuestionData.topic}
            </h1>
            
            <div className="flex items-center justify-center space-x-2 text-sm text-gray-600">
              <span className="px-3 py-1 bg-blue-50 text-blue-700 rounded-full">
                {currentQuestionData.category}
              </span>
              <span>â€¢</span>
              <span className="px-3 py-1 bg-green-50 text-green-700 rounded-full">
                {currentQuestionData.subcategory}
              </span>
            </div>
          </div>

          {/* Topic Progress Bar */}
          <div className="w-full bg-gray-200 rounded-full h-2 mb-4">
            <div 
              className="bg-gradient-to-r from-blue-500 to-purple-600 h-2 rounded-full transition-all duration-300"
              style={{ width: `${(completedInTopic / topicProgress) * 100}%` }}
            ></div>
          </div>
          
          <div className="text-center text-sm text-gray-600">
            {completedInTopic} of {topicProgress} questions completed in this topic
          </div>
        </div>

        {/* Question Form */}
        <div className="bg-white rounded-2xl shadow-lg p-8">
          {/* Question */}
          <div className="mb-8">
            <h2 className="text-xl font-semibold text-gray-800 mb-4 leading-relaxed">
              {currentQuestionData.question}
            </h2>
            
            {/* Answer Input */}
            <div>
              <div className="flex justify-between items-center mb-2">
                <label htmlFor="main-answer" className="block text-sm font-medium text-gray-700">
                  Your Answer *
                </label>
                <button
                  onClick={handleClear}
                  className="text-sm text-blue-600 hover:text-blue-800 underline"
                >
                  Clear Form
                </button>
              </div>
              
              <textarea
                ref={textareaRef}
                id="main-answer"
                name="main-answer"
                value={answer}
                onChange={handleAnswerChange}
                onFocus={() => console.log('Textarea focused')}
                placeholder="Please provide a detailed answer based on your knowledge and experience of your region..."
                className={`w-full px-4 py-3 border-2 rounded-lg focus:ring-2 focus:ring-blue-500 resize-none min-h-[120px] transition-colors ${
                  errors.answer ? 'border-red-300 focus:border-red-500' : 'border-gray-300 focus:border-blue-500'
                }`}
                maxLength={5000}
                disabled={isSaving}
              />
              
              <div className="flex justify-between items-center mt-2">
                {errors.answer && (
                  <p className="text-sm text-red-600">{errors.answer}</p>
                )}
                <p className="text-sm text-gray-500 ml-auto">
                  {answer.length}/5000 characters
                </p>
              </div>
            </div>
          </div>

          {/* Cultural Commonsense Question */}
          <div className="mb-8 p-6 bg-blue-50 rounded-xl border border-blue-200">
            <h3 className="font-semibold text-blue-900 mb-4">
              Cultural Commonsense Assessment
            </h3>
            <p className="text-sm text-blue-800 mb-4 leading-relaxed">
              Cultural commonsense, as conceptualized in anthropological and cognitive literature, refers to the set of 
              everyday beliefs, behaviors, values, and practices that are perceived as "natural," self-evident, and 
              widely shared within a cultural group.
            </p>
            <p className="font-medium text-blue-900 mb-4">
              Does this question pertain to cultural commonsense in your region? *
            </p>
            
            <div className="space-y-3">
              <div 
                className="flex items-center cursor-pointer"
                onClick={() => handleCulturalChange(true)}
              >
                <input
                  type="radio"
                  id="cultural-yes"
                  name="cultural-commonsense"
                  value="true"
                  checked={culturalCommonsense === true}
                  onChange={() => handleCulturalChange(true)}
                  disabled={isSaving}
                  className="h-4 w-4 text-blue-600 focus:ring-blue-500 border-gray-300"
                />
                <label htmlFor="cultural-yes" className="ml-3 text-gray-700 cursor-pointer">
                  Yes, this relates to cultural commonsense
                </label>
              </div>
              
              <div 
                className="flex items-center cursor-pointer"
                onClick={() => handleCulturalChange(false)}
              >
                <input
                  type="radio"
                  id="cultural-no"
                  name="cultural-commonsense"
                  value="false"
                  checked={culturalCommonsense === false}
                  onChange={() => handleCulturalChange(false)}
                  disabled={isSaving}
                  className="h-4 w-4 text-blue-600 focus:ring-blue-500 border-gray-300"
                />
                <label htmlFor="cultural-no" className="ml-3 text-gray-700 cursor-pointer">
                  No, this does not relate to cultural commonsense
                </label>
              </div>
            </div>
            
            {errors.cultural && (
              <p className="text-sm text-red-600 mt-2">{errors.cultural}</p>
            )}
          </div>

          {/* Success Message */}
          {showSuccess && (
            <div className="mb-6 p-4 bg-green-50 border border-green-200 rounded-lg">
              <div className="flex items-center">
                <span className="text-green-600 mr-2">âœ“</span>
                <span className="text-green-800 font-medium">Response saved successfully!</span>
              </div>
            </div>
          )}

          {/* Navigation Buttons */}
          <div className="flex justify-between items-center pt-6 border-t border-gray-200">
            <button
              onClick={handlePrevious}
              disabled={isFirstQuestion}
              className="flex items-center px-6 py-3 bg-gray-100 hover:bg-gray-200 disabled:bg-gray-50 disabled:text-gray-400 text-gray-700 font-medium rounded-lg transition-colors disabled:cursor-not-allowed"
            >
              <span className="mr-2">â†</span>
              Previous
            </button>

            <div className="flex space-x-4">
              <button
                onClick={handleSkip}
                className="px-6 py-3 bg-yellow-100 hover:bg-yellow-200 text-yellow-700 font-medium rounded-lg transition-colors"
              >
                Skip Question
              </button>

              <button
                onClick={handleSave}
                disabled={isSaving || !answer.trim() || culturalCommonsense === null}
                className="px-6 py-3 bg-blue-100 hover:bg-blue-200 disabled:bg-gray-100 text-blue-700 disabled:text-gray-400 font-medium rounded-lg transition-colors disabled:cursor-not-allowed"
              >
                {isSaving ? (
                  <div className="flex items-center">
                    <div className="animate-spin rounded-full h-4 w-4 border-b-2 border-blue-600 mr-2"></div>
                    Saving...
                  </div>
                ) : (
                  'Save'
                )}
              </button>

              <button
                onClick={handleNext}
                disabled={isSaving || !answer.trim() || culturalCommonsense === null}
                className="flex items-center px-6 py-3 bg-gradient-to-r from-blue-600 to-indigo-600 hover:from-blue-700 hover:to-indigo-700 disabled:from-gray-400 disabled:to-gray-500 text-white font-medium rounded-lg transition-all duration-200 disabled:cursor-not-allowed"
              >
                Save & Continue
                <span className="ml-2">â†’</span>
              </button>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}
