import React from 'react';
import { useForm } from '../context/FormContext.tsx';

export default function ProgressBar() {
  const { 
    state, 
    calculateProgress, 
    getTotalQuestionsInCurrentTopic, 
    getCompletedQuestionsInCurrentTopic 
  } = useForm();

  // Safety checks to prevent errors
  if (!state.questionsData || state.questionsData.length === 0) {
    return (
      <div className="bg-white border-b border-gray-200 px-6 py-4 sticky top-0 z-10 shadow-sm">
        <div className="max-w-4xl mx-auto">
          <div className="animate-pulse">
            <div className="h-4 bg-gray-200 rounded mb-2"></div>
            <div className="h-2 bg-gray-200 rounded"></div>
          </div>
        </div>
      </div>
    );
  }

  const overallProgress = calculateProgress();
  const totalInTopic = getTotalQuestionsInCurrentTopic();
  const completedInTopic = getCompletedQuestionsInCurrentTopic();
  const topicProgress = totalInTopic > 0 ? (completedInTopic / totalInTopic) * 100 : 0;

  // Get current navigation data safely
  const { categoryIndex, subcategoryIndex, topicIndex, questionIndex } = state.currentPosition;
  
  const currentCategory = state.questionsData[categoryIndex] || null;
  const currentSubcategory = currentCategory?.subcategories[subcategoryIndex] || null;
  const currentTopic = currentSubcategory?.topics[topicIndex] || null;

  // If we don't have valid data, show loading state
  if (!currentCategory || !currentSubcategory || !currentTopic) {
    return (
      <div className="bg-white border-b border-gray-200 px-6 py-4 sticky top-0 z-10 shadow-sm">
        <div className="max-w-4xl mx-auto">
          <div className="animate-pulse">
            <div className="h-4 bg-gray-200 rounded mb-2"></div>
            <div className="h-2 bg-gray-200 rounded"></div>
          </div>
        </div>
      </div>
    );
  }

  const currentQuestionNumber = questionIndex + 1;
  const totalQuestionsInTopic = currentTopic.questions.length;

  return (
    <div className="bg-white border-b border-gray-200 px-6 py-4 sticky top-0 z-10 shadow-sm">
      <div className="max-w-4xl mx-auto">
        {/* Breadcrumb Navigation */}
        <div className="text-sm text-gray-600 mb-3 flex flex-wrap items-center">
          <span className="font-medium text-blue-600 hover:text-blue-800 transition-colors">
            {currentCategory.category}
          </span>
          <span className="mx-2 text-gray-400">‚Üí</span>
          <span className="font-medium text-green-600 hover:text-green-800 transition-colors">
            {currentSubcategory.subcategory}
          </span>
          <span className="mx-2 text-gray-400">‚Üí</span>
          <span className="font-medium text-purple-600 hover:text-purple-800 transition-colors">
            {currentTopic.topic}
          </span>
          <span className="ml-auto text-xs bg-gray-100 text-gray-600 px-2 py-1 rounded-full">
            Question {currentQuestionNumber} of {totalQuestionsInTopic}
          </span>
        </div>

        {/* Current Topic Progress */}
        <div className="mb-4">
          <div className="flex items-center justify-between text-sm mb-2">
            <span className="font-medium text-gray-700">
              Current Topic Progress
            </span>
            <span className="text-gray-600 flex items-center">
              <span className="text-purple-600 font-semibold">{completedInTopic}</span>
              <span className="mx-1">/</span>
              <span>{totalInTopic}</span>
              <span className="ml-1">questions</span>
            </span>
          </div>
          <div className="w-full bg-gray-200 rounded-full h-2.5 overflow-hidden">
            <div 
              className="bg-gradient-to-r from-purple-500 to-pink-500 h-2.5 rounded-full transition-all duration-500 ease-out relative"
              style={{ width: `${Math.min(topicProgress, 100)}%` }}
            >
              {/* Animated shine effect */}
              <div className="absolute inset-0 bg-gradient-to-r from-transparent via-white to-transparent opacity-30 animate-pulse"></div>
            </div>
          </div>
          <div className="text-right text-xs text-gray-500 mt-1">
            {topicProgress.toFixed(1)}% of current topic
          </div>
        </div>

        {/* Overall Survey Progress */}
        <div className="mb-2">
          <div className="flex items-center justify-between text-sm mb-2">
            <span className="font-medium text-gray-700">
              Overall Survey Progress
            </span>
            <span className="text-gray-600 flex items-center">
              <span className="text-blue-600 font-semibold">{state.responses.size}</span>
              <span className="mx-1">/</span>
              <span>{state.progress.totalQuestions}</span>
              <span className="ml-1">total questions</span>
            </span>
          </div>
          <div className="w-full bg-gray-200 rounded-full h-3 overflow-hidden">
            <div 
              className="bg-gradient-to-r from-blue-500 to-indigo-600 h-3 rounded-full transition-all duration-700 ease-out relative"
              style={{ width: `${Math.min(overallProgress, 100)}%` }}
            >
              {/* Animated progress indicator */}
              <div className="absolute inset-0 bg-gradient-to-r from-transparent via-white to-transparent opacity-25 animate-pulse"></div>
            </div>
          </div>
          <div className="flex justify-between items-center text-xs text-gray-500 mt-1">
            <span>{overallProgress.toFixed(1)}% complete</span>
            <span>
              {state.progress.totalQuestions - state.responses.size} questions remaining
            </span>
          </div>
        </div>

        {/* Milestone Celebrations */}
        {overallProgress > 0 && (
          <>
            {/* Quarter milestones */}
            {overallProgress >= 25 && overallProgress < 26 && (
              <div className="mt-3 text-center">
                <span className="inline-flex items-center px-3 py-1 rounded-full text-sm font-medium bg-green-100 text-green-800 animate-bounce-in">
                  üéâ Quarter way there! Keep going!
                </span>
              </div>
            )}
            {overallProgress >= 50 && overallProgress < 51 && (
              <div className="mt-3 text-center">
                <span className="inline-flex items-center px-3 py-1 rounded-full text-sm font-medium bg-blue-100 text-blue-800 animate-bounce-in">
                  üöÄ Halfway done! You're doing great!
                </span>
              </div>
            )}
            {overallProgress >= 75 && overallProgress < 76 && (
              <div className="mt-3 text-center">
                <span className="inline-flex items-center px-3 py-1 rounded-full text-sm font-medium bg-purple-100 text-purple-800 animate-bounce-in">
                  ‚≠ê Three quarters complete! Almost there!
                </span>
              </div>
            )}

            {/* Topic completion celebration */}
            {topicProgress === 100 && (
              <div className="mt-3 text-center">
                <span className="inline-flex items-center px-3 py-1 rounded-full text-sm font-medium bg-yellow-100 text-yellow-800 animate-bounce-in">
                  ‚ú® Topic completed! Moving to the next one!
                </span>
              </div>
            )}
          </>
        )}

        {/* Estimated time remaining */}
        {overallProgress > 5 && (
          <div className="mt-2 text-center">
            <span className="text-xs text-gray-500">
              {getEstimatedTimeRemaining(overallProgress)}
            </span>
          </div>
        )}
      </div>
    </div>
  );
}

// Helper function for time estimation
function getEstimatedTimeRemaining(progress: number): string {
  if (progress === 0) return '';
  
  const averageTimePerQuestion = 2; // 2 minutes per question
  const remainingPercentage = 100 - progress;
  const estimatedMinutes = Math.ceil((remainingPercentage / 100) * 585 * averageTimePerQuestion);
  
  if (estimatedMinutes < 60) {
    return `~${estimatedMinutes} minutes remaining`;
  } else {
    const hours = Math.floor(estimatedMinutes / 60);
    const minutes = estimatedMinutes % 60;
    return `~${hours}h ${minutes}m remaining`;
  }
}
